args:
echo ".selection-box {
    position: absolute;
    background-color: var(--selection-bg);
    border: 1px solid var(--selection-border);
    pointer-events: none;
    z-index: 9999;
}" >> style.css

echo ".sky-wallpaper.is-sunrise {
    background: linear-gradient(to bottom, #38bdf8, #f472b6, #fbbf24);
}

.sky-wallpaper.is-sunset {
    background: linear-gradient(to bottom, #1e40af, #7c3aed, #ef4444);
}" >> style.css
